import axios from "axios";
import { useEffect, useState } from "react";
import styled from "styled-components";
import {API_KEY} from "../App";

const Container = styled.div`
display: flex;
flex-direction: row;
padding: 20px 30px;
justify-content: center;
border-bottom: 1px solid #969696;
`;

const CoverImage = styled.img`
object-fit: cover;
height: 352px;
`;

const InfoColumn = styled.div`
display: flex;
flex-direction: column;
margin: 20px;
`;

const MovieName = styled.span`
font-size: 22px;
font-weight: 600;
color: black;
margin: 15px 0;
white-space: nowrap;
text-transform: capitalize;
text-overflow: ellipsis;
overflow: hidden;
& span {
    opacity: 0.8;
}
`;

const MovieInfo = styled.span`
font-size: 16px;
font-weight: 500;
color: black;
overflow: hidden;
margin: 4px 0;
text-transform: capitalize;
text-overflow: ellipsis;
& span {
    opacity: 0.5;
}
`;

const Close = styled.span`
  font-size: 16px;
  font-weight: 600;
  color: black;
  background: lightgray;
  height: fit-content;
  padding: 8px;
  border-radius: 50%;
  cursor: pointer;
  opacity: 0.8;
`;


const MovieInfoComponent = (props) => {

    // NOTE: The API key is ?i instead of ?s as we are searching for a particular imdbID rather than movie Title.
    // As shown in API documentation.

    const { selectedMovie } = props;
    const [movieInfo, setMovieInfo] = useState();

    useEffect(() => {
        axios.get(`https://www.omdbapi.com/?i=${selectedMovie}&apikey=${API_KEY}`,)
        .then((response) => setMovieInfo(response.data)); 
    }, [selectedMovie]);

    // Assign the API content to the movie prop which
    // currently contains the type and index of search results!
    //const { Title, Year, imdbID, Type, Poster } = props.movie;

    return (
    <Container>
        <CoverImage src={movieInfo?.Poster}/>
            <InfoColumn>
                <MovieName>Movie: Iron Man</MovieName>
                <MovieInfo>IMDB Rating: <span>8.7</span></MovieInfo>
            </InfoColumn>
        </Container>
    );
};



export default MovieInfoComponent;
