import { useThemeProps } from '@mui/system';
import React from 'react';

/*
    {
        from: 'user'
        msg: 'hi'
        topic: 'imdb_ID'
    }
*/


const CIX = React.createContext();

const initState = {
    general:[
        {from: 'Ed', }
    ]
}

function reducer(state, action){
    const {from, msg, topic} = action.payload;
    
    switch (action.type){
        case 'RECIVE_MESSAGE':
            return{
                ...state,
                [topic]: [
                    ...state[topic],
                    {
                        from,
                        msg
                    }
                ]
            }
        default:
            return state
    }
}


export default function Store(props){
    
    const reducerHook = React.useReducer(reducer, initState);
    
    return(
        <CIX.Provider value={reducerHook}>
            {props.children}
        </CIX.Provider>
    )
}
