import { useState } from 'react';
import styled from 'styled-components'
import MovieComponent from "./components/MovieComponent";

const Header = styled.div`
display: flex;
flex-direction: row;
justify-content: space-between;
align-items: center;
background-color: black;
color: white;
padding: 10px;
font-size: 25px;
font-weight: bold;
box-shadow: 0 3px 6px 0 #555;
`;

const AppName = styled.div`
display: flex;
flex-direction: row;
align-items: center;
`;

const AppLogo = styled.img`
width: 48px;
height: 48px;
margin: 15px;
`;

const SearchBox = styled.div`
display: flex;
flex-direction: row;
padding: 10px 10px;
background-color: #fff;
border-radius: 6px;
margin-left: 20px;
margin-right: 15px;
width: 50%;
height: 25px;
align-items: center;
`;

const SearchIcon = styled.img`
 width: 32px;
 height: 30px;
 opacity: 40%;
 cursor: pointer;
`;

const SearchInput = styled.input`
color: black;
font-size: 20px;
font-weight: bolder;
width: 80%;
border: none;
outline: none;
margin-left: 12px;
text-transform: capitalize;
`;

const MovieListContainer = styled.div`
display: flex;
flex-direction: row;
flex-wrap: wrap;
padding: 30px;
justify-content: space-evenly;
`;

function App() {
  const [searchQuery, updateSearchQuery] = useState();
  const [timeoutId, updateTimeoutId] = useState();

  // API call
  const API_KEY = "7a9c166d";
  const fetchData = async (searchString) => {
      const response = await axios.get(
      'https://www.omdbapi.com/?s=${searchString}}&apikey=${API_KEY}')
  };




  // search query with timeout of .5 seconds so it doesn't
  // do an api call for every letter, but waits for
  // the user to finish typing.

  const onTextChange = (event) => {
    clearTimeout(timeoutId);
    updateSearchQuery(event.target.value);
    const timeout = setTimeout(() => fetchData(event.target.value), 500);
    updateTimeoutId(timeout);
  };





  return (
   <>

      <Header>
      <AppName>
        <AppLogo src="/VideocutLOGO.png" />
        React Movie App
      </AppName>
      <SearchBox>
        <SearchIcon src="/icons8-search.png" />
        <SearchInput placeholder="Search here..." 
        value={searchQuery}
        onChange={onTextChange} />
      </SearchBox>

      </Header>
    <MovieListContainer>
      <MovieComponent />
      <MovieComponent />
      <MovieComponent />
      <MovieComponent />
      <MovieComponent />
    </MovieListContainer>

      </>
  );
}

export default App;
