*/


export const CTX = React.createContext();


const initState = {
    general:[
        {from: 'Ed', msg: 'Hello'},
        {from: 'Dave', msg: 'Hello'},
        {from: 'Smith', msg: 'Hello'},
    ]   
}

function reducer(state, action){
    const {from, msg, topic} = action.payload;
    
    switch (action.type){
        case 'RECEIVE_MESSAGE':
            
            return{
                ...state,
                [topic]: [
                    ...state[topic],
                    {
                        from,
                        msg
                    }
                ]
            }
        default:
            return state
    }
}

